import { Component } from '@angular/core';
import dataObj from '../assets/api-response.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'rouse-challenge';
  data: DataPoint[];
  modelId: string | undefined;
  selectedModel: DataPoint | undefined;

  constructor() {
    this.data = []

    const keys = Object.keys(dataObj) as (keyof typeof dataObj)[];
    keys.forEach((id) => {
      // let yearsArr: Year[] = []
      const dataPoint = dataObj[id]

      // const years = Object.keys(dataPoint.schedule.years) as (keyof typeof dataPoint.schedule.years)[];
      // years.forEach((year) => { 
      //   const thisYear = dataPoint.schedule.years[year]
      //   yearsArr.push({
      //     year,
      //     marketRatio: thisYear['marketRatio'],
      //     auctionRatio: thisYear['auctionRatio'],
      //   })
      // })
      // dataPoint.schedule.yearsArray = yearsArr
      this.data.push({
        id,
        data: dataPoint
      });
    });
  }

  selectModel() { 
    this.selectedModel = this.data.filter((d) => d.data.classification.model === this.modelId)[0]
    console.log('!', this.selectedModel)
  }


}

interface DataPoint  {
  id: string,
  data: {
    schedule: {
      // yearsArray: Year[];
      years: any;
      defaultMarketRatio: number;
      defaultAuctionRatio: number;
    };
    saleDetails: {
      cost: number;
      retailSaleCount: number;
      auctionSaleCount: number;
    };
    classification: {
      category: string;
      subcategory: string;
      make: string;
      model: string;
    };
  };
};

