import { Component } from '@angular/core';
import dataObj from '../assets/api-response.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'rouse-challenge';
  data: DataPoint[];
  modelId: string | undefined;
  year: string | undefined;
  selectedModel: DataPoint | undefined;
  selectedModelYear: Year | undefined;
  marketValue: number | undefined;
  auctionValue: number | undefined;

  constructor() {
    this.data = []

    const keys = Object.keys(dataObj) as (keyof typeof dataObj)[];
    keys.forEach((id) => {
      const dataPoint = dataObj[id]
      this.data.push({
        id,
        data: dataPoint
      });
    });
  }

  selectModel() { 
    this.selectedModel = this.data.filter((d) => d.data.classification.model === this.modelId)[0]
  }

  selectModelYear() { 
    if (this.year) this.selectedModelYear = this.selectedModel?.data.schedule.years[this.year]
    if (this.selectedModel && this.selectedModelYear) {
      let cost = this.selectedModel.data.saleDetails.cost
      this.marketValue = cost * this.selectedModelYear.marketRatio
      this.auctionValue = cost * this.selectedModelYear.auctionRatio
    }
  }


}

interface DataPoint  {
  id: string,
  data: {
    schedule: {
      years: any;
      defaultMarketRatio: number;
      defaultAuctionRatio: number;
    };
    saleDetails: {
      cost: number;
      retailSaleCount: number;
      auctionSaleCount: number;
    };
    classification: {
      category: string;
      subcategory: string;
      make: string;
      model: string;
    };
  };
};

interface Year {
    year: string
    marketRatio: number;
    auctionRatio: number;
};

<h1>Challenge</h1>
<hr>
<h2>One</h2>
<form action="">
  <label for="ModelId">ModelId</label>
  <select [(ngModel)]="modelId" name="ModelId" (change)="selectModel()">
    <option *ngFor="let point of data" [value]="point.data.classification.model">{{point.data.classification.model}}
    </option>
  </select>
  <label for="year">Year</label>
  <select [(ngModel)]="year" name="year" (change)="selectModelYear()">
    <option 
      *ngFor="let year of selectedModel?.data?.schedule?.years | keyvalue " 
      [value]="year.key">{{year.key}} 
    </option>
  </select>
  ID: {{selectedModel?.id}}
</form>
  <div>
    marketValue: {{marketValue | currency}}
    auctionValue: {{auctionValue | currency}}
  </div>
