import { Component } from '@angular/core';
import dataObj from '../assets/api-response.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'rouse-challenge';
  data: DataPoint[];
  modelIds: any[];
  constructor() {
    this.data = []
    this.modelIds = []

    const keys = Object.keys(dataObj) as (keyof typeof dataObj)[];
    keys.forEach((id) => {
      console.log(id, dataObj[id]);
      const years = Object.keys(dataObj[id].schedule.years) as (keyof Year)[];
      const dataPoint = dataObj[id]
      let yearsArr: Year[] = []

      years.forEach((year) => { 
        yearsArr.push({
          year,
          marketRatio: dataObj[id].schedule.years[year].marketRatio,
          auctionRatio: dataObj[id].schedule.years[year].auctionRatio,
        })
      })
      dataPoint.schedule.yearsArray = yearsArr
      this.data.push({
        id,
        data: dataPoint
      });
    });
  }


}

interface DataPoint  {
  id: string,
  data: {
    schedule: {
      yearsArray: Year[];
      defaultMarketRatio: number;
      defaultAuctionRatio: number;
    };
    saleDetails: {
      cost: number;
      retailSaleCount: number;
      auctionSaleCount: number;
    };
    classification: {
      category: string;
      subcategory: string;
      make: string;
      model: string;
    };
  };
};

interface Year {
    year: string
    marketRatio: number;
    auctionRatio: number;
};
